import React, { useState, useEffect } from "react";
import PersonIndex from "../persons/PersonIndex";
import { apiGet } from "../utils/api";
import { Link } from "react-router-dom";

const PersonStatistics = ({ items }) => {
    const [personStats, setPersonStats] = useState([]);

    useEffect(() => {
        apiGet("/api/persons/statistics")
            .then(data => {
                console.log("Statistiky osob:", data);
                setPersonStats(data);
            })
            .catch(error => console.error("chyba při načítání statistik:", error));

    }, []);

    return (

        <div className="container">
            <div className="row g-3 d-flex">
                {personStats.map((stat, index) => (
                    <div className="card d-flex justify-content-center align items-cen" key={stat.personId}>
                    <h3>{stat.personName}</h3>
                    <p>Příjmy: {stat.revenue} Kč</p>
                  </div>


                ))}
            </div>
        </div>

    );
}
export default PersonStatistics;
