import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import { apiGet } from "../utils/api";
import PersonDetail from "../persons/PersonDetail";
import dateStringFormatter from "../utils/dateStringFormatter";

const InvoiceDetail = () => {
    const {id} = useParams();
    const [invoice, setInvoice] = useState();

    useEffect(() => {
        apiGet("/api/invoices/" + id)
        .then(data => {
            setInvoice(data)
        })
        .catch(error => {
            console.error(error);
        });
    }, [id]);

    if(!invoice){
        return <p>Načítám...</p>
    }
    return(
        <>
        <div>
            <h1>Detail faktury</h1>
            <hr />

            <h3>Produkt: {invoice.product}</h3>

            <p><strong>Číslo faktury:</strong>
                <br/>
                {invoice.invoiceNumber}
            </p>

            <p><strong>Dodavatel:</strong>
                <br/>
                {invoice.seller.name}
                /**
                 */
            </p>

            <p><strong>Nakupující:</strong>
                <br/>
                {invoice.buyer.name}
            </p>
            <p><strong>Datum vystavení faktury:</strong>
                <br/>
                {invoice.issued}
            </p> 

            <p><strong>Datum splatnosti faktury:</strong>
                <br/>
                {invoice.dueDate}
            </p> 
            <p><strong>Částka bez DPH</strong>
                <br/>
                {invoice.price}
            </p> 
            <p><strong>DPH</strong>
                <br/>
                {invoice.vat}
            </p> 
            <p><strong>Poznámky</strong>
                <br/>
                {invoice.note}
            </p> 



        </div>
        </>
    );




};
export default InvoiceDetail;
