import React, { useState, useEffect } from "react";
import PersonIndex from "../persons/PersonIndex";
import { apiGet } from "../utils/api";
import { Link } from "react-router-dom";

const PersonStatistics = ({items}) => {
    const[personStats, setPersonStats] = useState([]);

    useEffect(() => {
        apiGet("/api/person/statistics")
        .then(data => {
            console.log("Statistiky osob:", data);
            setPersonStats(data);
        })
        .catch(error => console.error("chyba při načítání statistik:", error));
        
    }, []);

    return(

        <div className="container">
            <div className="row g-3">
                {personStats.map((stat, index) => (
                    <div className="col-md-6 col-lg-12" key={stat + 1}>
                        <div className="card shadow-sm h-100">
                                                    <div className="card-body position-relative">
                                                        <div className="d-flex justify-content-between align-items-center mb-2">
                                                            <h5 className="card-title mb-0">
                                                                {(page * ITEMS_PER_PAGE) + index + 1} - {stat.name}
                                                                </h5>
                                                            <div className="dropdown">
                                                                <button
                                                                    className="btn btn-primary btn-sm dropdown-toggle"
                                                                    type="button"
                                                                    id={`dropdownMenuButton-${person._id}`}
                                                                    data-bs-toggle="dropdown"
                                                                    aria-expanded="false"
                                                                >
                                                                    Akce
                                                                </button>
                                                                <ul
                                                                    className="dropdown-menu"
                                                                    aria-labelledby={`dropdownMenuButton-${person._id}`}
                                                                >
                                                                    <li>
                                                                        <Link
                                                                            to={"/persons/show/" + person._id}
                                                                            className="dropdown-item btn btn-secondary"
                                                                        >
                                                                            Zobrazit
                                                                        </Link>
                                                                    </li>
                                                                    <li>
                                                                        <Link
                                                                            to={"/persons/edit/" + person._id}
                                                                            className="dropdown-item"
                                                                        >
                                                                            Upravit
                                                                        </Link>
                                                                    </li>
                                                                    <li>
                                                                        <button
                                                                            onClick={() => 
                        
                                                                                deletePerson(person._id)
                                                                            }
                                                                            className="dropdown-item text-danger"
                                                                        >
                                                                            Odstranit
                                                                        </button>
                                                                    </li>
                                                                </ul>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>

                    </div>
                ))}
            </div>
        </div>

    )
}
export default PersonStatistics;
