import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import { apiGet } from "../../utils/api";

const PurchasesInvoice = () => {
    const { identificationNumber, buyer, seller } = useParams();
    const [invoices, setInvoices] = useState({});

    useEffect(() => {
        if (!identificationNumber) return;

        apiGet(`/api/identification/${identificationNumber}/purchases`)
            .then(data => {
                console.log(data);
                setInvoices(data);
            })
            .catch(error => {
                console.error(error);
            });

    }, [identificationNumber]);


    return (
        
            <div>
                <h1>Výpis přijatých faktur pro IČ: {identificationNumber}</h1>
                <hr />
                {invoices && invoices.length === 0 ? (
                    <p>Žádné přijaté faktury</p>
                ) : (
                <table className="table table-striped">
                     <thead>
                        <tr>
                            <th>Číslo faktury</th>
                            <th>Dodavatel</th>
                            <th>Produkt</th>
                            <th>Vystaveno</th>
                            <th>Datum splatnosti</th>
                            <th>Cena</th>
                            <th>DPH</th>
                        </tr>
                    </thead>
                    <tbody>
                {invoices.map(invoice =>(
                    <tr key={invoice._id}>
                    <td>{invoice.invoiceNumber}</td>
                    <td>{invoice.buyer.name}</td>
                    <td>{invoice.product}</td>
                    <td>{invoice.issued}</td>
                    <td>{invoice.dueDate}</td>
                    <td>{invoice.price} Kč</td>
                    <td>{invoice.vat} %</td>
                </tr>
                ))}
                </tbody>
                
                </table>
                )}
            </div>

        
    )
}
export default PurchasesInvoice;
